select distinct A.cad,B.stampdate,B.stamptime,B.unitin,B.unitout,B.device_number,B.status,B.activitysubject,B.memo from ct_al_transaction as A left join ct_al_transaction as B on A.cad=B.cad left join ct_al_transaction AS C on C.cad=B.cad where A.activitysubject='SIGALERT BEGIN' and ( C.activitysubject='OPEN INCIDENT' AND (C.memo ~ '^405[- ]*[N] ' or C.memo ~ '^[N][- ]*405 ' ) ) order by B.stampdate,B.stamptime
